import React from 'react';
import { useEffect, useReducer } from 'react';
import axios from 'axios';
import Row from 'react-bootstrap/Row';
const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return { ...state, loading: false, product: action.playload };
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.playload };
    default:
      return state;
  }
};

export default function HomeScreen() {
  const [{ loading, error, product }, dispatch] = useReducer(reducer, {
    loading: true,
    error: '',
    product: [],
  });

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await axios.get('/api/products');
        dispatch({ type: 'FETCH_SUCCESS', playload: result.data });
      } catch (err) {
        dispatch({ type: 'FETCH_FATL', playload: err.message });
      }
    };
    fetchData();
  }, []);
  return (
    <div>
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>error</div>
      ) : (
        <Row>
          {product.map((res) => {
            return <div>{res.name}</div>;
          })}
        </Row>
      )}
    </div>
  );
}
