import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';
import Rating from './Rating';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
export default function Product(props) {
  const { product } = props;
  return (
    <Container>
      <Card className="product text-dark bg-light size">
        <Row>
          <Link to={`/product/${product.slug}`}>
            <img
              src={product.image}
              className="card-img-top "
              alt={product.name}
            ></img>
          </Link>
        </Row>

        <Card.Body>
          <Row>
            <Link to={`/product/${product.slug}`}>
              <Card.Title className="f">{product.name}</Card.Title>
            </Link>
          </Row>
          <Row>
            <Rating
              rating={product.rating}
              numReviews={product.numReviews}
              key={product.slug}
            ></Rating>
          </Row>
          <Row>
            <Card.Text>${product.price}</Card.Text>
          </Row>
          <Row className="row align-items-end">
            <div className="col  col-8 mx-auto ">
              <Button className="btn-success">Add to cart</Button>
            </div>
          </Row>
        </Card.Body>
      </Card>
    </Container>
  );
}
