import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';
import Rating from './Rating';

export default function Product(props) {
  const { product } = props;
  return (
    <Card className="product text-dark bg-light size">
      <Link to={`/product/${product.slug}`}>
        <img
          src={product.image}
          className="card-img-top "
          alt={product.name}
        ></img>
      </Link>
      <Card.Body>
        <Link to={`/product/${product.slug}`}>
          <Card.Title className="f">{product.name}</Card.Title>
        </Link>
        <Rating
          rating={product.rating}
          numReviews={product.numReviews}
          key={product.slug}
        ></Rating>
        <Card.Text>${product.price}</Card.Text>

        <div className="d-grid  col-8 mx-auto position-relative">
          <Button className="btn-success position-absolute bottom-100 end-50">
            Add to cart
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
}
